import React, { Component } from 'react';

class MemeGenerator extends Component {
    constructor() {
        super()
        this.state = {
            topText: "",
            bottomText: "",
            randomImg: "http://i.imgflip.com/1bij.jpg",
            allMemesImgs: []
        }

        this.handleChange = this.handleChange.bind(this)

    }

    //Getting Data with lifecycle
    componentDidMount(){
       fetch("https://api.imgflip.com/get_memes")
          .then(response => response.json())
          .then(response => {
              const {memes} = response.data
              console.log(memes[0]) 
              this.setState({allMemesImgs: memes})

          })
    }


    //onChange handler Event method for the inputs
    handleChange(event){

        // console.log("working!")

        //Get the name from the attribute name from the inputs of the boxes
        const {name, value} = event.target;

        //Update the state with the value from the box
        this.setState({ [name]: value});


    }

        render() {
            return(
                // <h1>MEME GENERATOR SECTION</h1>

                <div>
                    <form className="meme-form">

                    <input 
                        type="text"
                        name="topText"
                        placeholder="Top Text"
                        value={this.state.topText}
                        onChange={this.handleChange}
                    />

                     <input 
                        type="text"
                        name="bottomText"
                        placeholder="Bottom Text"
                        value={this.state.bottomText}
                        onChange={this.handleChange}
                    />


                    <button>Gen</button>

                    </form>

                    <div className="meme">
                        <img src={this.state.randomImg} alt="" />
                        <H2 className="top"></H2>
                        <H2 className="bottom"></H2>
                    </div>

                </div>
            )
        }
}
export default MemeGenerator;
